import React, { useState } from 'react';
import axios from 'axios';

const LoginRegistration = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(isLogin ? '/api/login' : '/api/register', {
        email,
        password,
      });

      // Handle successful login/registration
      console.log(response.data);
    } catch (error) {
      // Handle error
      console.error(error);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ width: '400px', padding: '40px', backgroundColor: '#1f2a3a', borderRadius: '8px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '30px' }}>
          <img src="book-icon.svg" alt="Book Icon" style={{ width: '60px' }} />
        </div>
        <h1 style={{ color: 'white', textAlign: 'center', marginBottom: '30px' }}>
          {isLogin ? 'Book Rent' : 'Sign Up'}
        </h1>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{
              padding: '12px 16px',
              borderRadius: '4px',
              border: 'none',
              backgroundColor: '#2c3849',
              color: 'white',
              fontSize: '16px',
            }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{
              padding: '12px 16px',
              borderRadius: '4px',
              border: 'none',
              backgroundColor: '#2c3849',
              color: 'white',
              fontSize: '16px',
            }}
          />
          <button
            type="submit"
            style={{
              padding: '12px 16px',
              borderRadius: '4px',
              border: 'none',
              backgroundColor: '#007bff',
              color: 'white',
              fontWeight: 'bold',
              fontSize: '16px',
              cursor: 'pointer',
            }}
          >
            {isLogin ? 'Login' : 'Sign Up'}
          </button>
        </form>
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            marginTop: '20px',
            color: '#999999',
            cursor: 'pointer',
            fontSize: '14px',
          }}
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin ? 'Create an account' : 'Already have an account? Log in'}
        </div>
      </div>
    </div>
  );
};

export default LoginRegistration;
