import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import LoginPage from './Component/BookRent';
import AdminDashboard from './Component/AdminDashboard';
import OwnersList from './Component/BooksPage';
import OwnerDetails from './Component/OwnerDetails';

const MenuButton = ({ onClick }) => {
  return (
    <button
      onClick={onClick}
      style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        backgroundColor: '#333',
        color: '#fff',
        border: 'none',
        padding: '10px 15px',
        fontSize: '16px',
        cursor: 'pointer',
      }}
    >
      <i className="fas fa-bars"></i>
    </button>
  );
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    // Check if the user is already logged in
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setCurrentUser(JSON.parse(storedUser));
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (user) => {
    setCurrentUser(user);
    setIsLoggedIn(true);
    localStorage.setItem('currentUser', JSON.stringify(user));
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setIsLoggedIn(false);
    localStorage.removeItem('currentUser');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div className="App">
        <MenuButton onClick={toggleMenu} />
        {isMenuOpen && (
          <nav
            style={{
              position: 'absolute',
              top: '60px',
              left: '20px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
              padding: '10px 20px',
              zIndex: 1,
            }}
          >
            <Link to="/" style={{ display: 'block', color: '#333', textDecoration: 'none', padding: '10px 0' }}>
              Home
            </Link>
            <Link to="/login" style={{ display: 'block', color: '#333', textDecoration: 'none', padding: '10px 0' }}>
              Login
            </Link>
            <Link to="/admin-dashboard" style={{ display: 'block', color: '#333', textDecoration: 'none', padding: '10px 0' }}>
              Admin Dashboard
            </Link>
            <Link to="/owners-list" style={{ display: 'block', color: '#333', textDecoration: 'none', padding: '10px 0' }}>
              Owners List
            </Link>
            <Link to="/owner-details" style={{ display: 'block', color: '#333', textDecoration: 'none', padding: '10px 0' }}>
              Owner Details
            </Link>
          </nav>
        )}
        <Routes>
          <Route path="/" element={<Navigate to="/login" />} />
          <Route path="/login" element={<LoginPage handleLogin={handleLogin} />} />
          <Route path="/admin-dashboard" element={<AdminDashboard />} />
          <Route path="/owners-list" element={<OwnersList />} />
          <Route path="/owner-details" element={<OwnerDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
