import React, { useState } from 'react';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert('Login successful');
        // Optionally redirect to dashboard or another page
        window.location.href = '/dashboard'; // Example redirect
      } else {
        alert('Error: ' + data.error);
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('An error occurred while logging in');
    }
  };

  const styles = {
    container: {
      display: 'flex',
      height: '100vh',
      overflow: 'hidden',
    },
    leftPane: {
      flex: 1,
      backgroundColor: '#0B132B',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    rightPane: {
      flex: 1,
      padding: '5vw',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      maxWidth: '400px',
      margin: 'auto',
    },
    logo: {
      width: '80%',
      maxWidth: '300px',
      height: 'auto',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    input: {
      marginBottom: '1rem',
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: '1px solid #ccc',
    },
    button: {
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: 'none',
      backgroundColor: '#007BFF',
      color: 'white',
      cursor: 'pointer',
    },
    signupLink: {
      marginTop: '0.75rem',
      color: '#007BFF',
      textDecoration: 'none',
    },
    '@media (max-width: 768px)': {
      rightPane: {
        padding: '2rem',
      },
    },
    '@media (max-width: 480px)': {
      input: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
      button: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
      logo: {
        width: '60%',
      },
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftPane}>
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/6d/Book_font_awesome.svg/1024px-Book_font_awesome.svg.png"
          alt="Book Logo"
          style={styles.logo}
        />
      </div>
      <div style={styles.rightPane}>
        <h2>Book Rent</h2>
        <h3>Login</h3>
        <form style={styles.form} onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email address"
            style={styles.input}
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            style={styles.input}
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit" style={styles.button}>LOGIN</button>
          <a href="/register" style={styles.signupLink}>Don't have an account? Sign Up</a>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
