import React from 'react';

const LoginPage = () => {
  return (
    <div style={styles.container}>
      <div style={styles.imageSection}>
        <img 
          src="book_logo.png" 
          alt="Book Logo" 
          style={styles.logo}
        />
      </div>
      <div style={styles.formSection}>
        <h2 style={styles.title}>Book Rent</h2>
        <h4 style={styles.subtitle}>Login into Book Rent</h4>
        <form style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label} htmlFor="email">Email address</label>
            <input type="email" id="email" name="email" style={styles.input} defaultValue="john@gmail.com" />
          </div>
          <div style={styles.inputGroup}>
            <label style={styles.label} htmlFor="password">Password</label>
            <input type="password" id="password" name="password" style={styles.input} defaultValue="*********" />
          </div>
          <div style={styles.rememberMe}>
            <input type="checkbox" id="remember" name="remember" style={styles.checkbox} />
            <label htmlFor="remember" style={styles.rememberMeText}>Remember me</label>
          </div>
          <button type="submit" style={styles.button}>LOGIN</button>
        </form>
        <p style={styles.signUpText}>
          Haven't an account? <a href="#" style={styles.signUpLink}>Sign up</a>
        </p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh',
    width: '100vw',
  },
  imageSection: {
    flex: 1,
    backgroundColor: '#0A1F44',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  logo: {
    width: '200px',
    height: '200px',
  },
  formSection: {
    flex: 1,
    backgroundColor: '#FFFFFF',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: '32px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  subtitle: {
    fontSize: '18px',
    marginBottom: '20px',
  },
  form: {
    width: '300px',
    display: 'flex',
    flexDirection: 'column',
  },
  inputGroup: {
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    marginBottom: '8px',
    fontSize: '14px',
  },
  input: {
    width: '100%',
    padding: '10px',
    fontSize: '14px',
    borderRadius: '4px',
    border: '1px solid #CCCCCC',
  },
  rememberMe: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '20px',
  },
  checkbox: {
    marginRight: '8px',
  },
  rememberMeText: {
    fontSize: '14px',
  },
  button: {
    padding: '10px',
    fontSize: '14px',
    color: '#FFFFFF',
    backgroundColor: '#007BFF',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  signUpText: {
    marginTop: '20px',
    fontSize: '14px',
  },
  signUpLink: {
    color: '#007BFF',
    textDecoration: 'none',
  }
};

export default LoginPage;
