import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', { email, password });
      // Handle successful login, e.g., store the JWT token and redirect to the dashboard
    } catch (error) {
      // Handle login error, e.g., display an error message
    }
  };

  return (
    <div className="container-fluid vh-100">
      <div className="row h-100">
        <div className="col-md-6 bg-primary d-flex justify-content-center align-items-center">
          <img src="book_logo.png" alt="Book Logo" className="img-fluid" style={{ width: '200px', height: '200px' }} />
        </div>
        <div className="col-md-6 d-flex justify-content-center align-items-center">
          <div>
            <h2 className="mb-3">Book Rent</h2>
            <h4 className="mb-4">Login into Book Rent</h4>
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email address</label>
                <input type="email" className="form-control" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input type="password" className="form-control" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
              </div>
              <div className="form-check mb-3">
                <input className="form-check-input" type="checkbox" id="remember" name="remember" checked={remember} onChange={(e) => setRemember(e.target.checked)} />
                <label className="form-check-label" htmlFor="remember">Remember me</label>
              </div>
              <button type="submit" className="btn btn-primary">LOGIN</button>
            </form>
            <p className="mt-3">
              Haven't an account? <a href="#" className="text-primary">Sign up</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
