import React from 'react';

const LoginPage = () => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      overflow: 'hidden',
    },
    leftPane: {
      flex: '1 1 50%',
      backgroundColor: '#0B132B',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    rightPane: {
      flex: '1 1 50%',
      padding: '2rem',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
    },
    logo: {
      width: '80%',
      maxWidth: '300px',
      height: 'auto',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    input: {
      marginBottom: '1rem',
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: '1px solid #ccc',
    },
    checkboxContainer: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '1rem',
    },
    checkbox: {
      marginRight: '0.5rem',
    },
    button: {
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: 'none',
      backgroundColor: '#007BFF',
      color: 'white',
      cursor: 'pointer',
    },
    loginLink: {
      marginTop: '0.75rem',
      color: '#007BFF',
      textDecoration: 'none',
    },
    '@media (max-width: 768px)': {
      container: {
        flexDirection: 'column',
      },
      leftPane: {
        flex: 'none',
        height: '30vh',
        padding: '1rem',
      },
      rightPane: {
        flex: 'none',
        padding: '1rem',
      },
      logo: {
        width: '60%',
      },
    },
    '@media (max-width: 480px)': {
      input: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
      button: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftPane}>
        <img 
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/6d/Book_font_awesome.svg/1024px-Book_font_awesome.svg.png" 
          alt="Book Logo" 
          style={styles.logo} 
        />
      </div>
      <div style={styles.rightPane}>
        <h2>Book Rent</h2>
        <h3>Signup as Owner</h3>
        <form style={styles.form}>
          <input type="email" placeholder="Email address" style={styles.input} />
          <input type="password" placeholder="Password" style={styles.input} />
          <input type="password" placeholder="Confirm Password" style={styles.input} />
          <input type="text" placeholder="Location" style={styles.input} />
          <input type="text" placeholder="Phone Number" style={styles.input} />
          <div style={styles.checkboxContainer}>
            <input type="checkbox" style={styles.checkbox} />
            <label>I accept the Terms and Conditions</label>
          </div>
          <button type="submit" style={styles.button}>SIGN UP</button>
          <a href="/login" style={styles.loginLink}>Already have an account? Login</a>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
