import React, { useState } from 'react';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here
    console.log('Logging in with:', { email, password, rememberMe });
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="bg-[#1f2a3a] text-white p-10 rounded-md w-full max-w-md">
        <div className="flex justify-center mb-8">
          <img src="book-icon.svg" alt="Book Icon" className="w-16" />
        </div>
        <h1 className="text-center text-2xl mb-6">Book Rent</h1>
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-4 py-3 rounded-md bg-[#2c3849] border-none text-white"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-3 rounded-md bg-[#2c3849] border-none text-white"
          />
          <div className="flex items-center">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
              className="mr-2"
            />
            <span className="text-[#999999] text-sm">Remember me</span>
          </div>
          <button
            type="submit"
            className="w-full bg-[#007bff] text-white font-bold py-3 rounded-md cursor-pointer"
          >
            Login
          </button>
        </form>
        <div className="text-center mt-4 text-[#999999] text-sm cursor-pointer">
          Haven't got an account? Sign up
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
