import React from 'react';
import logo from 'A:/CF2/Book_Rent/book-rent/src/download.jpeg';
const LoginPage = () => {
  const styles = {
    container: {
      display: 'flex',
      height: '100vh',
      overflow: 'hidden',
    },
    leftPane: {
      flex: 1,
      backgroundColor: '#0B132B',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    rightPane: {
      flex: 1,
      padding: '5vw', // Relative units for responsiveness
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      maxWidth: '600px', // Constrain width on larger screens
      margin: 'auto', // Center form on larger screens
    },
    logo: {
      width: '80%',
      maxWidth: '300px',
      height: 'auto',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    input: {
      marginBottom: '1rem',
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: '1px solid #ccc',
    },
    button: {
      padding: '0.75rem',
      fontSize: '1rem',
      borderRadius: '0.5rem',
      border: 'none',
      backgroundColor: '#007BFF',
      color: 'white',
      cursor: 'pointer',
    },
    signupLink: {
      marginTop: '0.75rem',
      color: '#007BFF',
      textDecoration: 'none',
    },
    '@media (max-width: 768px)': {
      rightPane: {
        padding: '2rem', // Adjusted padding for smaller screens
      },
    },
    '@media (max-width: 480px)': {
      input: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
      button: {
        padding: '0.5rem',
        fontSize: '0.875rem',
      },
      logo: {
        width: '60%',
      },
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftPane}>
        <img 
          src={logo}
          alt="Book Logo" 
          style={styles.logo} 
        />
      </div>
      <div style={styles.rightPane}>
        <h2>Book Rent</h2>
        <h3>Login</h3>
        <form style={styles.form}>
          <input type="email" placeholder="Email address" style={styles.input} />
          <input type="password" placeholder="Password" style={styles.input} />
          <button type="submit" style={styles.button}>LOGIN</button>
          <a href="/signup" style={styles.signupLink}>Don't have an account? Sign Up</a>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
